import React from 'react';
import styled from 'styled-components';
import LineHour from './LineHour';

const ContainerHours = styled.div`
  margin-top: 80px;
  margin-left: 30px;
  height: 950px;
  width: 400px;
`;

const setHour = (hour) => {
  hourActual = `${hour} AM`;
  if(hour == 12) {hourActual = `${hour} PM`}
  if(hour > 12) {hourActual = `${hour - 12} PM`}

  return hourActual
}

const DateMonth = () => {
  const hours = [];
  let hourActual;
  const totalHours = 24;

  for (let hour = 1; hour < totalHours; hour++) {

    hours.push(<LineHour text={hourActual}/>);
  }

  return (
    <ContainerHours>
      {hours}
    </ContainerHours>
  )
}

export default DateMonth;
