import React, { useEffect } from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom';
import { addArticle, allArticles } from '../../actions/articles';
import ArticlePagination from './ArticlePagination';

const ArticleList = (props) => {
  useEffect(() => {
    props.listArticles(props.currentPage, props.elementsByPage);
  }, [])  

  const checkPages = () => {
    const PLUSTOPAGE = 1;
    let client_page = 0;
    let pagination = [];
    let pages = [...Array(props.pages).keys()];

    pages.forEach(page => {
      client_page = page + plusToPage;
      if(props.currentPage == client_page){
        return pagination.push(<ArticlePagination currentColor="gray" number={client_page} key={page}/>);
      }
      pagination.push(<ArticlePagination number={client_page} key={page}/>);
    });
    return pagination
  }

  return (
    <div>
      {
        props.articles.map((article) => {
          return (
            <div key={article.id}>
              <h2><Link to={`/articles/${article.id}`}>{article.title}</Link></h2>
              {article.content}
              <hr />
            </div>
          );
        })
      }
      {
        checkPages()
      }
      <hr/>
      <button className="btn btn-outline-primary"><Link to={`/articles/new`}>New</Link></button>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    articles: state.articlesReducer.articles,
    pages : state.articlesReducer.pages,
    elementsByPage : state.articlesReducer.elementsByPage,
    currentPage : state.articlesReducer.currentPage
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    addArticle: (title, content) => dispatch(addArticle(title, content)),
    listArticles: (page, quantity_elements) => dispatch(allArticles(page, quantity_elements))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ArticleList);
