import {
  ADD_ARTICLE,
  EDIT_ARTICLE,
  INFO_ARTICLE,
  REMOVE_ARTICLE,
  ALL_ARTICLES,
  CHANGE_PAGES,
  CHANGE_QUANTITY,
  MOVE_PAGE
} from '../constants/actionTypes';

const articlesState = {
  articles: [],
  article: {},
  pages: 0,
  elementsByPage: nil,
  currentPage: 1
};

function articlesReducer(state = articlesState, action) {
  switch (action.type) {
    case ADD_ARTICLE:
      return [
        ...state.articles,
        {
          id: 'fff',
          title: action.title,
          content: action.content
        }
      ];
    case EDIT_ARTICLE:
      return {

      };
    case INFO_ARTICLE:
      return {
        ...state,
        article: action.article
      };
    case REMOVE_ARTICLE:
      return {

      };
    case ALL_ARTICLES:
      return {
        ...state,
        articles: action.articles.articles,
        currentPage: action.articles.current_page,
        elementsByPage: action.articles.elements_by_page,
        pages: action.articles.pages
      }
    case CHANGE_PAGES: 
      return {
        pages: action.pages
      };
    case CHANGE_QUANTITY: 
      return {
        quantity_elements: action.quantity_elements
      };
    case MOVE_PAGE: 
      return {
        actual_page: action.actual_page
      };
    default:
      return state;
  }
}

export default articlesReducer;
